import React from "react";
import { formatDistanceToNow, differenceInDays } from "date-fns";

const Task = ({ taskObj, onComplete }) => {
  // Deadline'i bir tarih nesnesine dönüştürün
  const deadlineDate = new Date(taskObj.deadline);

  // Kaç gün kaldığını hesaplayın
  const daysUntilDeadline = differenceInDays(deadlineDate, new Date());

  return (
    <div className="task">
      <h3>{taskObj.title}</h3>
      <div className="deadline">
        Son teslim:{" "}
        <span>
          {distanceToNow} ({daysUntilDeadline} gün kaldı)
        </span>
      </div>
      <p>{taskObj.description}</p>
      <div>
        {taskObj.people.map((p) => (
          <span className="pill" key={p}>
            {p}
          </span>
        ))}
      </div>
      {onComplete && (
        <button onClick={() => onComplete(taskObj.id)}>Tamamlandı</button>
      )}
    </div>
  );
};

export default Task;
